from Code.ESAPI import ESAPI, on_subscribe


@on_subscribe
def on_subscribe_func(data):
    # 这些里写处理行情的函数，该函数为回调函数
    print('自定义函数接收到信息', data)


def test_api():
    # 登录并订阅
    api = ESAPI(on_subscribe_func)
    auth = ''
    api.login(auth=auth,
              ip='61.163.243.173',
              port=7171,
              username='ES',
              password='123456')
    api.subscribe(0, 'COMEX', 'F', 'GC', '2106')  # 可以订阅多个


def exe():
    ip = input('输入IP:')
    port = input('输入端口:')
    username = input('输入用户名:')
    password = input('输入密码:')
    auth = input('输入授权码(不输入为默认):')
    if not auth.strip():
        auth = ''
    api = ESAPI(on_subscribe_func)
    api.login(auth=auth,
              ip=ip,
              port=port,
              username=username,
              password=password)
    while 1:
        subscribe = input('输入订阅(比如COMEX F GC 2106):')
        subscribe = subscribe.split(' ')
        api.subscribe(0, subscribe[0], subscribe[1], subscribe[2], subscribe[3])  # 可以订阅多个


if __name__ == '__main__':
    exe()
